INSERT INTO `revision` (`page_id`,`rev_id`,`text`,`user_id`,`user_text`,`major`,`created`,`tags`,`deleted`) VALUES (22,1,'  * 상위항목 [[SICP]]\r\n\r\n== Chapter 1 ==\r\n=== 1.3 ===\r\n<code>\r\n(define (ss x y)\r\n  (+ (* x x)\r\n     (* y y)))\r\n\r\n(define (test x y z)\r\n  (cond ((and (> x z) (> y z)) (ss x y))\r\n        ((and (> y x) (> z x)) (ss y z))\r\n        ((and (> z y) (> x y)) (ss z x))))\r\n</code>\r\n\r\n2015/7/19\r\n',NULL,'::ffff:192.168.35.1',0,'2017-06-04 22:21:24.000',NULL,0);
