INSERT INTO `revision` (`page_id`,`rev_id`,`text`,`user_id`,`user_text`,`major`,`created`,`tags`,`deleted`) VALUES (68,2,'  * 상위항목 [[ProjectEuler]]\r\n== 12 번 ==\r\n{{{syntax(python)\r\nprimes = [2, 3, 5, 7, 9, 11, 13, 17, 19, 23, 29]\r\n\r\ndef primeFacorization(n):\r\n    primeDivisors = {}\r\n    while True:\r\n        if n==1:\r\n            return primeDivisors #finish prime factorization.\r\n\r\n        for i in range(len(primes)):\r\n            prime = primes[i]\r\n            if n%prime == 0:\r\n                if prime in primeDivisors.keys():\r\n                    primeDivisors[prime] += 1\r\n                else:\r\n                    primeDivisors[prime] = 1\r\n\r\n                n /= prime\r\n                break #start from first prime.\r\n            if i == len(primes)-1:\r\n                addPrime(primes)\r\n\r\ndef numberOfDivisors(primeDivisors):\r\n    result = 1\r\n    for i in primeDivisors.values():\r\n        result *= (i+1)\r\n\r\n    return result\r\n\r\ndef addPrime(primes):\r\n    i = primes[len(primes)-1]\r\n    if(i==2):\r\n        i = 1;\r\n\r\n    while True:\r\n        i += 2\r\n        if(isPrime(i)):\r\n            primes.append(i)\r\n            return primes\r\n\r\ndef isPrime(n):\r\n    if((n != 2) and (n%2==0)):\r\n        return False; #even number\r\n\r\n    i = 3;\r\n    while(i*i < n):\r\n        if n%i == 0:\r\n            return False\r\n        i += 2\r\n    return True\r\n\r\ndef main():\r\n    n = 10\r\n    stepNumber = 5\r\n\r\n    while True:\r\n        numOfDivisors = numberOfDivisors(primeFacorization(n))\r\n\r\n        print(n , numOfDivisors)\r\n        n += stepNumber\r\n        stepNumber += 1\r\n\r\n        if(numOfDivisors >= 500):\r\n            break\r\n            \r\nmain()\r\n}}}\r\n\r\n== 13번 ==\r\n[[LISP]]을 사용하면 편하다.\r\n\r\n{{{syntax(scheme)\r\n#lang scheme\r\n(define (sum list) (if (pair? list)\r\n                       (+ (car list) (sum (cdr list)))\r\n                       0))\r\n\r\n(sum \'