INSERT INTO `revision` (`page_id`,`rev_id`,`text`,`user_id`,`user_text`,`major`,`created`,`tags`,`deleted`) VALUES (151,1,'====== 기독교의 기원 ======\r\n고양이를 너무나도 사랑하는 냥덕들이 만든 종교가 Catholic, 즉 캣홀릭이며, 이 중에서 개가 더 좋다며 개를 숭상하는 사람들이 갈라져나온 종교를 개신교라고 합니다.\r\n\r\n출처: 트위터 @7HhH77I',1,'::ffff:192.168.35.1',0,'2017-12-31 23:55:25.000',NULL,0);
INSERT INTO `revision` (`page_id`,`rev_id`,`text`,`user_id`,`user_text`,`major`,`created`,`tags`,`deleted`) VALUES (152,1,'====== 같은 말 다른 의미 ======\r\n푸틴과 김정일이 회담을 가졌다.\r\n이런 저런 얘기가 오가던 도중 서로가 대동하고 있는 경호훤들의 용맹함에 대한 얘기가 나왔다.\r\n자존심 강한 들은 자신의 경호원이 더 용감하다며 열을 내며 싸우다가\r\n분을 삭이지 못한 푸틴이 자신의 경호원에게 지금 당장 창밖으로 투신하라고 명령했다.\r\n그러자 푸틴의 경호원이 애절한 목소리로 사정했다.\r\n\"저에게는 아내와 아이가 있습니다..\"\r\n그 광경을 본 김정일이 코웃음을 치며 자신의 경호원에게 뛰어 내리라고 말하자\r\n북한의 경호원은 한점 망설임도 없이 창문을 향해 뛰는 것이었다.\r\n너무 놀란 러시아 경호원이 북한 경호원을 뒤에서 끌어안고 말리자\r\n북한 경호원은 발버둥치며 초리쳤다.\r\n\"이거 놓으라! 나에게는 아내와 아이가 있단 말이야!\"\r\n',1,'::ffff:192.168.35.1',0,'2017-12-31 23:55:39.000',NULL,0);
