  `checked` tinyint(1) DEFAULT '0',
  PRIMARY KEY (`isbn13`),
  KEY `idx_titles` (`series_id`),
  KEY `fk_books_publisher_id_idx` (`publisher_id`),
  CONSTRAINT `fk_books_publisher_id` FOREIGN KEY (`publisher_id`) REFERENCES `publishers` (`id`) ON DELETE NO ACTION ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `caching` (
