  PRIMARY KEY (`page_id`),
  UNIQUE KEY `title_UNIQUE` (`cat_title`),
  CONSTRAINT `fk_cat_page_id` FOREIGN KEY (`page_id`) REFERENCES `page` (`page_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `categorylink` (
