CREATE TABLE IF NOT EXISTS `namespace` (
  `ns_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `ns_title` varchar(45) NOT NULL,
  `ns_PAC` tinyint(3) unsigned NOT NULL DEFAULT '15' COMMENT 'public access control\ncreate(8) read(4) update(2) delete(1). default 15',
  `created` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`ns_id`),
  UNIQUE KEY `title_UNIQUE` (`ns_title`)
) ENGINE=InnoDB AUTO_INCREMENT=103 DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `page` (
  `page_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `ns_id` int(11) unsigned NOT NULL,
  `page_title` varchar(255) NOT NULL,
  `page_PAC` tinyint(3) unsigned DEFAULT NULL COMMENT 'public access control\ncreate(8) read(4) update(2) delete(1). default null.',
  `rev_id` int(11) unsigned DEFAULT NULL,
  `cached` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `redirect` varchar(255) DEFAULT NULL,
  `deleted` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `touched` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`page_id`),
  UNIQUE KEY `title_UNIQUE` (`ns_id`,`page_title`),
  KEY `fk_rev_id_idx` (`rev_id`),
  CONSTRAINT `fk_page_ns_id` FOREIGN KEY (`ns_id`) REFERENCES `namespace` (`ns_id`) ON UPDATE CASCADE,
  CONSTRAINT `fk_page_rev_id` FOREIGN KEY (`rev_id`) REFERENCES `revision` (`rev_id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=156 DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `pagelink` (
  `from` int(11) unsigned NOT NULL,
