  CONSTRAINT `fk_pagel_to_page_id` FOREIGN KEY (`to`) REFERENCES `page` (`page_id`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `people` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
