  `birthdate` date DEFAULT NULL,
  `deathdate` date DEFAULT NULL,
  `link` varchar(255) DEFAULT NULL,
  `nationality` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=686 DEFAULT CHARSET=utf8;

